import React, {Component, useState} from "react"
import {Guild} from '../utils/types'
import {
  Button,
  DropdownFooter,
  DropdownHeader,
  DropdownItem,
  DropdownItemContainer,
  Input,
} from "../utils/styles";
interface TextProps {
  title: string;
  guild: any;
  default: string
}
function update(text,title:string,guild:any) {
  axios.put('/api/guild/'+guild.id+'/config',{title,value:text})
}

export function TextInput(props: TextProps) {
  const input = React.createRef();
  const [text,setText]=useState(props.default)
  return   <div>
    <input type="text" text={text ? text : props.default} ref={input} onChange={()=>setText(input.current.value || props.default)} />
    <Button style={{ marginRight: "5px" }} disabled={!selected} onClick={()=>update(text,props.title,props.guild)}>
              Save
            </Button></div>
}
